const payload = $input.first().json.text;

const emojis = payload.match(/\p{Emoji_Presentation}|\p{Extended_Pictographic}/gu) || [];

const EMOJI_MAP = $input.first().json.emoji_map;

function mapEmoji(e) {
  return EMOJI_MAP[e] || `a motif inspired by ${e}`;
}

const motifs = (emojis.length ? emojis.slice(0,3) : ['🍋','🗼','🌧️']).map(mapEmoji);

let template = `a vintage travel postcard from a parallel universe featuring ${motifs.join(', ')}, richly textured print stock, realistic paper grain, no readable text`;

const prompt = `${template}, cinematic composition, global illumination, physically-plausible materials, award-winning photography, 35mm depth of field, highly detailed`;


return [{
  json: {
    result: prompt
  }
}];

const user_msg = $input.first().json.message.text ;
const emojis = user_msg.match(/\p{Emoji_Presentation}|\p{Extended_Pictographic}/gu) || [];

if (emojis.length === 0) {
  return [{ json: { 
    error: "No emojis found. Please include at least one emoji.", 
    code: 404 
    }
  }];
}

return $input.all();
